import React, { useEffect, useState } from "react";
import Delete from "./components/deletBtn";
import Edit from "./components/editBtn";
import Axios from "axios";
import "./App.css";

const App = () => {
  const [data, setData] = useState([]);
  // const [postData, setPostData] = useState("");

  const fetchData = async () => {
    try {
      const response = await Axios.get("http://127.0.0.1:8080/");
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const PostData = async () => {
    try {
      await Axios.post("http://127.0.0.1:8080/", { ToDo: postData });
      fetchData(); // Fetch data after successful post request
      setPostData(""); // Clear the input field
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchData(); // Fetch data when the component mounts
  }, []);

  return (
    <>
      <main>
        {/* <input
          onChange={(e) => {
            setPostData(e.target.value);
          }}
          type="text"
          name="toDo"
          value={postData}
        />
        <button onClick={PostData}>Add</button> */}
        <div>
          <ul>
            {data.map((item, index) => {
              return (
                <li key={index}>
                  {item.ToDo}
                  <Delete />
                  <Edit />
                </li>
              );
            })}
          </ul>
        </div>
      </main>
    </>
  );
};

export default App;
