import Axios from "axios";
import React, { useEffect, useState } from "react";
import Delete from "./deletBtn";
import Edit from "./editBtn";

const Display = (props) => {
  const [data, setData] = useState([]);
  const [edit, setfirst] = useState()
  const fetchData = async () => {
    try {
      const response = await Axios.get("http://127.0.0.1:8080/");
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);

  const sendData = () => {
    props.myFunc(fetchData());
  }

  return (
    <>
      <div>
        <ul>
          {data.map((item, index) => {
            return (
              <li key={index}>
                {item.ToDo}
                <Delete />
                <Edit />
              </li>
            );
          })}
        </ul>
      </div>
    </>
  );
};

export default Display;
