import React, { useEffect, useState } from "react";
import Delete from "./components/deletBtn";
import Edit from "./components/editBtn";
import Axios from "axios";
import "./App.css";

const App = () => {
  const [data, setData] = useState([]);
  const [postData, setPostData] = useState([]);

    const PostData = async () => {
      try {
        await Axios.post("http://127.0.0.1:8080/",{ToDo: postData});
      } catch (error) {
        console.error(error);
      }
    };

    PostData();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await Axios.get("http://127.0.0.1:8080/");
        console.log(response.data[0].ToDo);
        setData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);
  return (
    <>
      <main>
        <form>
          <input onChange={(e)=>{
            setPostData(e.target.value)
          }} type="text" name="toDo" />
          <button>Add</button>
          <div>
            <ul>
              {data.map((item, index) => {
                return (
                  <li key={index}>
                    {item.ToDo}
                    <Delete />
                    <Edit />
                  </li>
                );
              })}
            </ul>
          </div>
        </form>
      </main>
    </>
  );
};

export default App;
